(function(){tinymce.PluginManager.requireLangPack('h5p');tinymce.create('tinymce.plugins.H5P',{init:function(ed,url){var openH5PDialog=function(){ed.windowManager.open({file:ed.getParam("moodle_plugin_base")+'h5p/dialog.php',width:520,height:250,inline:1},{plugin_url:url})};ed.addCommand('mceH5P',openH5PDialog);ed.addButton('tinymce_h5p',{title:'H5P Plugin',cmd:'mceH5P',image:url+'/img/tinymce_h5p.png'});ed.contentCSS.push(ed.getParam("moodle_plugin_base")+'/h5p/tinymce_h5p.css');ed.onDblClick.add(function(ed,e){var n=ed.selection.getNode();if(n.nodeName=='DIV'&&n.className=='h5p-placeholder'){openH5PDialog()}})},getInfo:function(){return{longname:'H5P plugin',author:'Kateryna Degtyariova',authorurl:'https://catalyst-au.net/',infourl:'',version:"1.0"}}});tinymce.PluginManager.add('h5p',tinymce.plugins.H5P)})();var editor_tinymce_filepicker=M.editor_tinymce.filepicker;var h5p_tinymce_browser_callback=function(target_id,url,type,win){if(type=='h5p'){YUI().use('core_filepicker',function(Y){var editor_id=tinyMCE.selectedInstance.editorId;if(editor_id=='mce_fullscreen'){editor_id=tinyMCE.selectedInstance.settings.elements}
var options=options=M.editor_tinymce.filepicker_options[editor_id].h5p;options.formcallback=M.editor_tinymce.filepicker_callback;options.editor_target=win.document.getElementById(target_id);M.core_filepicker.show(Y,options)})}else{editor_tinymce_filepicker(target_id,url,type,win)}}